commits.content:
/* AllStatusUpdatesPage.css */

body {
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 0;
}

.container {
    max-width: 600px;
    margin: 0 auto;
}

.header {
    background-color: #00aced;
    color: white;
    padding: 10px;
    text-align: center;
}

.navbar {
    background-color: #00aced;
    color: white;
    padding: 10px;
    text-align: center;
}

.status-update {
    border: 1px solid #ddd;
    padding: 10px;
    margin: 10px 0;
    background-color: white;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
}

.status-update p {
    margin: 5px 0;
}

.status-update a {
    color: #00aced;
    text-decoration: none;
    font-weight: bold;
}

.status-update a:hover {
    text-decoration: underline;
}
